import React, { useState, useEffect } from "react";
import Course from "./components/Course";
import base_url from "././api/BootApi";
import axios from "axios";
import { toast } from "react-toastify";

export default function ViewCourse() {
  useEffect(() => {
    //This is used to change the title of the website
    document.title = "All Courses";
  }, []);

  // function to call server
  const getAllCoursesFromServer = () => {
    axios.get(`${base_url}/courses`).then(
      (response) => {
        //success
        // console.log(response.data);
        toast.success("Courses Loaded Successfully", {
          theme: "colored",
          position: "top-right",
        });
        setCourses(response.data);
      },
      (error) => {
        //error
        console.log(error);
        toast.error("Something Went Wrong");
      }
    );
  };

  //calling loading course function
  useEffect(() => {
    getAllCoursesFromServer();
  }, []);

  const [courses, setCourses] = useState([]);
  
  // After deleting course
  // const updateCourses = (id) => {
  //   setCourses(courses.filter((c) => c.id != id));
  // };
  return (
    <>
      <h1 className="text-center mt-5">ALL COURSES</h1>
      <h4 className="text-center mt-2  ">
        List of Courses Available are followed:
      </h4>

      {courses.length > 0
        ? courses.map((item) => <Course key={item.id} course={item} update ={updateCourses}/>)
        : "NO COURSES AVAILABLE CURRENTLY"}
    </>
  );
}
