import React, { useEffect, useState } from "react";
import { Form, FormGroup, Input ,Label,Button,check} from "reactstrap";

function AddCourse() {
 
  useEffect(()=>{
    //This is used to change the title of the website
    document.title = "Add Course";
  },[])

  conts[course ,setCourse] = useState(
    {}
  );

  //FormHandler function
  const handleForm= (e) =>
  {
    console.log(course);
    e.preventDefault();
  }
  return (
    <>
    <h1 className="text-center mt-5"  >Add New Course: </h1>
      <Form onSubmit = {handleForm} className="text-center mt-5">
        <FormGroup >
          <Label for="userId">Course ID</Label>
          <Input className="text-center"
            type="text"
            placeholder="Enter userId Here"
            name="userId"
            id="userId"
            onChange={()=>
            {
              setCourse({...course,id:e.target.value});
            }}
          ></Input>
        </FormGroup>

        <FormGroup>
    <Label for="courseTitle"  >
    Course Title
    </Label>
    <Input className="text-center"
      id="courseTitle"
      name="courseTitle"
      placeholder="Enter Course Title Here"
      type="text"
      onChange={()=>
        {
          setCourse({...course,title:e.target.value});
        }}
    />
  </FormGroup>
     

  <FormGroup  >
    <Label for="exampleText">
      Course Description
    </Label>
    <Input className="text-center"
      id="CourseDescription"
      name="text"
      type="textarea"
      placeholder="Enter Course Description Here"
      style={{height:200} }
      onChange={()=>
        {
          setCourse({...course,descr:e.target.value});
        }}
    />
  </FormGroup>

  
  <Button type = "submit" color="success mx-2" >
    Add Course
  </Button>
  <Button color="warning  " >
    Clear
  </Button>
      </Form>
    </>
  );
}
export default AddCourse;
