import React, { useState,useEffect } from "react";
import Course from "./components/Course";
import base_url from "./../api/BootApi"
import axios from "axios"

export default function ViewCourse() {

  useEffect( ()=>
  {
      //This is used to change the title of the website
      document.title = "All Courses"
  },[])
  const [courses, setCourses] = useState([
    {
      title: "JAVA COURSE",
      description: "This is A JAVA course for beginners",
    },
    {
      title: "PYTHON COURSE",
      description: "This is A PYTHON course for beginners",
    },
    {
      title: "REACTJS COURSE",
      description: "This is A REACTJS course for beginners",
    },
    {
      title: "ANGULARJS COURSE",
      description: "This is A ANGULARJS course for beginners",
    },
  ]);
  return (
    <>
      <h1 className="text-center mt-5">ALL COURSES</h1>
      <h4 className="text-center mt-2  ">List of Courses Available are followed:</h4>

      {courses.length > 0
        ? courses.map((item) => <Course course={item} />)
        : "NO COURSES AVAILABLE CURRENTLY"}
    </>
  );
}
