import React from "react";
 

function HeroSection() {
  
  return (
    <section   style={{ background:'#30336b' }}>
      {/* Hero Section  */}
      <div className="container mx-auto flex px-5 py-24 items-center justify-center flex-col">
        {/* Main Content  */}
        <main>
          <div className="text-center">
            <div className="mb-2">
              {/* Image  */}
              <div className="flex justify-center">
                <img
                  src="https://cdn-icons-png.flaticon.com/128/3685/3685253.png"
                  alt=""
                />
              </div>

              {/* Text  */}
              <h1 className=" text-3xl text-white font-bold">Courses APP</h1>
            </div>

            {/* Paragraph  */}
            <p
               
              className="sm:text-3xl text-xl font-extralight sm:mx-auto "
            >
              Here are some blogs and tutorials contributed by Aditya.
            </p>
          </div>
        </main>
      </div>
    </section>
  );
}

export default HeroSection;
