    document.title = "Home Page";
  }, []);
  return (
    <>
      <HeroSection />
      <h2 class="title">ALL COURSES</h2>
      <h4 className="text-center mt-2  ">
        List of Courses Available are followed:
      </h4>
      <div style={{display:"flex" ,   }}> 
      <ViewCourse />
      </div>
    </>
  );
}
export default Home;
